function goToByScroll(a){$("html,body").animate({scrollTop:$(a).offset().top},"slow")}$(document).ready(function(){$(window).bind("resize",a);function a(){var b=$(window).width();if(b>768){$(document).bind("scroll",function(){var c=$(this).scrollTop();if(c>500){$("#header").addClass("sticky-header").css({height:100})}else{$("#header").removeClass("sticky-header");if(c>300&c<500){$("#header").css({height:0})}else{$("#header").css({height:100})}}})}else{$(document).unbind("scroll");$("#header").removeClass("sticky-header").css({height:86})}}a();$(".mobile-panel-open").click(function(){$("#mobile-panel-flyout").addClass("open");$(".mobile-panel-close").show();$(".mobile-panel-open").hide()});$(".mobile-panel-close").click(function(){$("#mobile-panel-flyout").removeClass("open");$(".mobile-panel-close").hide();$(".mobile-panel-open").show()});$(".jump select").on("change",function(){if($(this).val()!=""){document.location=$(this).val()}});$(".home #page-content .btn").click(function(b){b.preventDefault();goToByScroll($(this).attr("href"))});$(".flexslider").flexslider({animation:"fade",controlNav:false});$(".responsive-table").each(function(){var b=$(this).find("th");$(this).find("tr").each(function(){$(this).find("td").each(function(c){$(this).attr("data-label",$(b[c]).text().trim());if($(this).text().trim()==""){$(this).addClass("empty")}})})})});